return (
    <div className='new-expense'>
      {!inputPanel && (
        <button onClick={openPanel}>Add New Expense</button>
      )}
      {inputPanel && (
        <ExpenseForm
        onSaveForm={inputSaveHandler}
        onClose={closePanel}
        />
      )}
    </div>
  );
};
