import React, { useState } from "react";
import './ExpenseForm.css';
export function ExpenseForm() {
    // ez az első módszer, amikor különböző state-eket definiálunk

    // const [enteredTitle, setEnteredTitle] = useState("");
    // const [enteredAmount, setEnteredAmount] = useState("");
    // const [enteredDate, setEnteredDate] = useState("");

    // const titleChangeHandler = (event) => {
    //     setEnteredTitle(event.target.value);
    // }

    // const amountChangeHandler = (event) => {
    //     setEnteredAmount(event.target.value);
    // }

    // const dateChangeHandler = (event) => {
    //     setEnteredDate(event.target.value);

    //ez a második módszer, amikor egy state-et definiálunk, de egy objecttel

    // const [userInput, setUserInput] = useState({
    //     enteredTitle: "",
    //     enteredAmount: "",
    //     enteredDate: ""
    // })
    // const titleChangeHandler = (event) => {
    //     setUserInput({           //ez is különbség, hogy nem setEnteredTitle
    //         ...userInput,
    //         enteredTitle: event.target.value
    //     });
    // }
    // const amountChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredAmount: event.target.value
    //     });
    // }
    // const dateChangeHandler = (event) => {
    //     setUserInput({
    //         ...userInput,
    //         enteredDate: event.target.value
    //     });
    // }

    //ez a harmadik módszer, amikor a state update nagyon függ a previous state-től

    const [userInput, setUserInput] = useState({
        enteredTitle: "",
        enteredAmount: "",
        enteredDate: ""
    })
    const titleChangeHandler = (event) => {
        setUserInput((prevState) => {
            return {
                ...prevState, 
                enteredTitle: event.target.value
            }
        });
    }
    const amountChangeHandler = (event) => {
        setUserInput((prevState) => {
            return {
                ...prevState,
                enteredAmount: event.target.value
            }
        });
    }
    const dateChangeHandler = (event) => {
        setUserInput((prevState) => {
            return {
                ...prevState,
                enteredDate: event.target.value
            }
        });
    }
    return (
        <form>
            <div className="new-expense__controls">
            <div className="new-expense__controls">
                <label>Title</label>
                <input type='text' onChange={titleChangeHandler} />
            </div>
            <div className="new-expense__controls">
                <label>Amount</label>
                <input type='number' min='0.01' step='0.01' onChange={amountChangeHandler} />
            </div>
            <div className="new-expense__controls">
                <label>Date</label>
                <input type='date' min='2019-01-01' max='2022-12-31' onChange={dateChangeHandler} />
            </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </form>
    )
}
